export default {
  data() {
    return {
      questions: [{
        id: 1,
        title: "What is the capital of France?",
        answers: [{
          id: 1,
          text: "Paris",
          correct: true
        }, {
          id: 2,
          text: "London"
        }, {
          id: 3,
          text: "Berlin"
        }]
      }, {
        id: 2,
        title: "What is the largest planet in our solar system?",
        answers: [{
          id: 4,
          text: "Jupiter",
          correct: true
        }, {
          id: 5,
          text: "Saturn"
        }, {
          id: 6,
          text: "Earth"
        }]
      }, {
        id: 3,
        title: "what duck has the most superpowers?",
        answers: [{
          id: 7,
          text: "regular duck",
          correct: true
        }, {
          id: 8,
          text: "Superduck"
        }, {
          id: 9,
          text: "bird"
        }]
      }
      // more questions
      ],

      question: {},
      showAnswer: false,
      answerMessage: ""
    };
  },
  mounted() {
    // select a random question
    this.question = this.questions[Math.floor(Math.random() * this.questions.length)];
  },
  methods: {
    checkAnswer(answerId) {
      const selectedAnswer = this.question.answers.find(answer => answer.id === answerId);
      this.showAnswer = true;
      this.showQuestion = false;
      this.currentAnswer = answer.correct ? "Correct!" : "Nah man that ain't correct, you should know this, try again";
      if (selectedAnswer.correct) {
        this.answerMessage = "Correct!";
      } else {
        this.answerMessage = "Nah man that ain't correct, you should know this, try again";
      }
    },
    async nextQuestion() {
      this.showAnswer = false;
      this.showQuestion = true;
      this.currentAnswer = "";
      // wait for 1 second before showing new question
      await new Promise(resolve => setTimeout(resolve, 1000));
      // select a random question
      this.currentQuestion = this.questions[Math.floor(Math.random() * this.questions.length)];
    }
  }
};
