import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex)

export default new Vuex.Store({
  state: {
    correctAnswers: 0
  },
  mutations: {
    correctAnswers(state, value) {
      state.correctAnswers = value
    }
  }
})
